sys_ui_action.script:
let  Caluculate = () => {
    //リダイレクト先を設定
    action.setRedirectURL(current);

    //選択肢リストの取得
    let choiceList = [];
    let choiceLabel = [];
    let choiceDict = {};
    let gr_choice = new GlideRecord('sys_choice');
    gr_choice.addQuery('name', 'x_547977_mnp_team_work_task');
    gr_choice.addQuery('element', 'u_work_summary');
    gr_choice.addQuery('inactive', 'false');
	gr_choice.addQuery('value','!=',100);
    gr_choice.orderBy('value');
    gr_choice.query();
    while (gr_choice.next()) {
        choiceList.push(gr_choice.value.slice());
        choiceLabel.push(gr_choice.label.slice());
    }

    //リザルトをクリア
    current.u_result = "";

    //集計対象者取得
    let workers = current.u_create_member.split(',');

    for (let i = 0; i < workers.length; i++) {
        //変数初期化
        let totalScore = 0;
		let name = "";
		let count = 0;
        //スコアの初期化
        for (let item in choiceList) {
            choiceDict[choiceList[item]] = 0;
        }

        let gr_workRP = new GlideRecord('x_547977_mnp_team_work_report');
        gr_workRP.addQuery('u_total_table_ref', current.sys_id);
        gr_workRP.addQuery('u_worker', workers[i]);
        gr_workRP.query();

        let gr_user = new GlideRecord('sys_user');
        gr_user.addQuery('sys_id', workers[i]);
        gr_user.query();
        if (gr_user.next()) {
            name = gr_user.last_name + ' ' + gr_user.first_name;
        }
        name = name ? name : "unknown";

        current.u_result += '【' + name + 'さんの集計結果】\n';
        while (gr_workRP.next()) {
            let gr_worktsk = new GlideRecord('x_547977_mnp_team_work_task');
            gr_worktsk.addQuery('u_parent_day', gr_workRP.sys_id);
			gr_worktsk.addQuery('u_work_summary','!=',100);
            gr_worktsk.query();

            while (gr_worktsk.next()) {
                totalScore += gr_worktsk.u_hour;
                choiceDict[gr_worktsk.u_work_summary] += gr_worktsk.u_hour;
            }
        }
        for (let key in choiceDict) {
            current.u_result += choiceLabel[count] + ':' + choiceDict[key] + '(H)\n';
            count++;
        }
        current.u_result += '合計時間:' + totalScore + '(H)\n';
        current.u_result += '------------------------------------------\n\n';
        gs.info(current.u_result);
    }
    current.update();
};

if (typeof window == 'undefined') {
    Caluculate();
}
